package com.beffjezos.carebuddy.adapters;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.recyclerview.widget.RecyclerView;import com.beffjezos.carebuddy.R;import com.beffjezos.carebuddy.models.Donation;import java.util.List;public class DonationAdaptetr extends RecyclerView.Adapter<DonationAdaptetr.MyViewHolder> {    private List<Donation> donationList;    private Context context;    public DonationAdaptetr(List<Donation> donationList, Context context) {        this.donationList = donationList;        this.context = context;    }    @Override    public MyViewHolder onCreateViewHolder(ViewGroup viewGroup, int i) {        View view = LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.item_donation, viewGroup, false);        return new MyViewHolder(view);    }    @Override    public void onBindViewHolder(MyViewHolder viewHolder, final int i) {        viewHolder.nameTV.setText(donationList.get(i).getDonorName());        viewHolder.desTv.setText(donationList.get(i).getSummary());        viewHolder.numberTv.setText(donationList.get(i).getDonorNum());        viewHolder.addressTv.setText(donationList.get(i).getDonorAddress());    }    @Override    public int getItemCount() {        return donationList.size();    }    public class MyViewHolder extends RecyclerView.ViewHolder {        TextView nameTV, addressTv, numberTv;        TextView desTv;        public MyViewHolder(View itemView) {            super(itemView);            nameTV = itemView.findViewById(R.id.nameCha);            desTv = itemView.findViewById(R.id.addressCha5);            addressTv = itemView.findViewById(R.id.addressCha);            numberTv = itemView.findViewById(R.id.desCha);        }    }}