package com.beffjezos.carebuddy.adapters;import android.annotation.SuppressLint;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageButton;import android.widget.ImageView;import android.widget.TextView;import android.widget.Toast;import androidx.recyclerview.widget.RecyclerView;import com.beffjezos.carebuddy.R;import com.beffjezos.carebuddy.models.Product;import java.util.List;import static com.beffjezos.carebuddy.ui.activities.DonatePackage.updatePackage;public class ProductAdapter extends RecyclerView.Adapter<ProductAdapter.MyViewHolder> {    private final List<Product> productList;    private final Context context;    public ProductAdapter(List<Product> productList, Context context) {        this.productList = productList;        this.context = context;    }    @Override    public MyViewHolder onCreateViewHolder(ViewGroup viewGroup, int i) {        View view = LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.item_editable_product, viewGroup, false);        return new MyViewHolder(view);    }    @SuppressLint("SetTextI18n")    @Override    public void onBindViewHolder(final MyViewHolder viewHolder, final int i) {        final Product product;        final int[] count = new int[1];        product = productList.get(i);        count[0] = product.getQuantity();        final long[] price = {product.getSinglePrice()};        viewHolder.nameTV.setText(product.getName());        viewHolder.priceTv.setText("৳ " + price[0] * count[0]);        viewHolder.logo.setImageResource(product.getIcon());        viewHolder.countTv.setText(count[0] + " " + product.getUnit());        viewHolder.up.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                count[0] = count[0] + 1;                product.setQuantity(count[0]);                viewHolder.priceTv.setText("৳ " + price[0] * count[0]);                viewHolder.countTv.setText(count[0] + " " + product.getUnit());                updatePackage(product, i);            }        });        viewHolder.down.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if (count[0] == 0) {                    Toast.makeText(context, "Hold on!", Toast.LENGTH_SHORT).show();                } else {                    count[0] = count[0] - 1;                    product.setQuantity(count[0]);                    viewHolder.priceTv.setText("৳ " + price[0] * count[0]);                    viewHolder.countTv.setText(count[0] + " " + product.getUnit());                    updatePackage(product, i);                }            }        });    }    @Override    public int getItemCount() {        return productList.size();    }    public class MyViewHolder extends RecyclerView.ViewHolder {        TextView nameTV, priceTv, countTv;        ImageView logo;        ImageButton up, down;        public MyViewHolder(View itemView) {            super(itemView);            nameTV = itemView.findViewById(R.id.nameCha);            priceTv = itemView.findViewById(R.id.nameCha2);            logo = itemView.findViewById(R.id.floatingActionButton3);            up = itemView.findViewById(R.id.imageButton);            down = itemView.findViewById(R.id.imageButton2);            countTv = itemView.findViewById(R.id.textView13);        }    }}