package com.beffjezos.carebuddy.models;public class Donation {    String donorName, summary, donorNum, charityId, donorId, donorAddress, type;    long moneyAmount;    Package aPackage;    public Donation() {    }    public Donation(String donorName, String summary, String donorNum, String charityId, String donorId, String donorAddress, String type, long moneyAmount) {        this.donorName = donorName;        this.summary = summary;        this.donorNum = donorNum;        this.charityId = charityId;        this.donorId = donorId;        this.donorAddress = donorAddress;        this.type = type;        this.moneyAmount = moneyAmount;    }    public String getDonorName() {        return donorName;    }    public void setDonorName(String donorName) {        this.donorName = donorName;    }    public String getSummary() {        return summary;    }    public void setSummary(String summary) {        this.summary = summary;    }    public String getDonorNum() {        return donorNum;    }    public void setDonorNum(String donorNum) {        this.donorNum = donorNum;    }    public String getCharityId() {        return charityId;    }    public void setCharityId(String charityId) {        this.charityId = charityId;    }    public String getDonorId() {        return donorId;    }    public void setDonorId(String donorId) {        this.donorId = donorId;    }    public String getDonorAddress() {        return donorAddress;    }    public void setDonorAddress(String donorAddress) {        this.donorAddress = donorAddress;    }    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }    public long getMoneyAmount() {        return moneyAmount;    }    public void setMoneyAmount(long moneyAmount) {        this.moneyAmount = moneyAmount;    }    public Package getaPackage() {        return aPackage;    }    public void setaPackage(Package aPackage) {        this.aPackage = aPackage;    }}