package com.beffjezos.carebuddy.models;public class Donor {    String id, name, address, number, gender;    String age;    public Donor(String id, String name, String address, String number, String gender, String age) {        this.id = id;        this.name = name;        this.address = address;        this.number = number;        this.gender = gender;        this.age = age;    }    public Donor() {    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }    public String getNumber() {        return number;    }    public void setNumber(String number) {        this.number = number;    }    public String getGender() {        return gender;    }    public void setGender(String gender) {        this.gender = gender;    }    public String getAge() {        return age;    }    public void setAge(String age) {        this.age = age;    }}