package com.beffjezos.carebuddy.models;import java.io.Serializable;import java.util.List;public class Package implements Serializable {    List<Product> productList;    long price;    String name, shortDes;    int logo;    public Package(List<Product> productList, String name, String shortDes, int logo) {        this.productList = productList;        this.name = name;        this.shortDes = shortDes;        this.logo = logo;    }    public List<Product> getProductList() {        return productList;    }    public void getTotalPrice(List<Product> productList) {        this.productList = productList;    }    public long getTotalPrice() {        long totalPrice = 0;        for (int i = 0; i < productList.size(); i++) {            totalPrice += productList.get(i).getPrice();        }        return totalPrice;    }    public void setPrice(long price) {        this.price = price;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getLogo() {        return logo;    }    public void setLogo(int logo) {        this.logo = logo;    }    public String getShortDes() {        return shortDes;    }    public void setShortDes(String shortDes) {        this.shortDes = shortDes;    }    public void setProductList(List<Product> productList) {        this.productList = productList;    }}