package com.beffjezos.carebuddy.models;import java.io.Serializable;public class Product implements Serializable {    String name;    long price;    int icon;    int quantity;    String unit;    public Product(String name, long price, int icon, int quantity, String unit) {        this.name = name;        this.price = price;        this.icon = icon;        this.quantity = quantity;        this.unit = unit;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public long getPrice() {        return price * quantity;    }    public long getSinglePrice() {        return price;    }    public void setPrice(long price) {        this.price = price;    }    public int getIcon() {        return icon;    }    public void setIcon(int icon) {        this.icon = icon;    }    public int getQuantity() {        return quantity;    }    public void setQuantity(int quantity) {        this.quantity = quantity;    }    public String getUnit() {        return unit;    }    public void setUnit(String unit) {        this.unit = unit;    }}