package com.beffjezos.carebuddy.ui.activities;import android.content.Intent;import android.os.Bundle;import androidx.appcompat.app.AppCompatActivity;import com.beffjezos.carebuddy.R;import java.util.Timer;import java.util.TimerTask;import static android.content.Intent.FLAG_ACTIVITY_CLEAR_TASK;public class Splash extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_splash);        new Timer().schedule(new TimerTask() {            @Override            public void run() {                startActivity(new Intent(Splash.this, MainActivity.class).setFlags(FLAG_ACTIVITY_CLEAR_TASK));            }        }, 3000);    }}