package com.beffjezos.carebuddy.ui.fragments;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import com.beffjezos.carebuddy.R;import com.beffjezos.carebuddy.adapters.PackageAdapter;import static com.beffjezos.carebuddy.ui.activities.MainActivity.AllProDuctpackageList;public class PackageFragment extends Fragment {    private RecyclerView recyclerView;    private RecyclerView.LayoutManager layoutManager;    private PackageAdapter packageAdapter;    @Nullable    @Override    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        return inflater.inflate(R.layout.fragment_package, container, false);    }    @Override    public void onStart() {        super.onStart();    }    @Override    public void onViewCreated(View view, @Nullable Bundle savedInstanceState) {        recyclerView = view.findViewById(R.id.fef);        layoutManager = new LinearLayoutManager(getContext());        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));        recyclerView.setLayoutManager(layoutManager);        recyclerView.setHasFixedSize(true);        for (int i = 0; i < AllProDuctpackageList.size(); i++) {            packageAdapter = new PackageAdapter(AllProDuctpackageList, getContext());            recyclerView.setAdapter(packageAdapter);            packageAdapter.notifyDataSetChanged();        }    }}